import React, { Component } from "react";

class Summary extends Component {

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      invoiceItems: [],
      transactionsItems: []
    };
  }

  componentDidMount() {
    // Fetch transactionSummary
    fetch('http://localhost:8080/transactionsSummary/1000')
        .then(res => res.json())
        .then(
            result => {
              this.setState({
                isLoaded: true,
                transactionsItems: result
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            error => {
              this.setState({
                isLoaded: true,
                error
              });
            }
        );

    // Get Invoice Summary
    fetch('http://localhost:8080/invoiceSummary/1000')
        .then(res => res.json())
        .then(
            result => {
              this.setState({
                isLoaded: true,
                invoiceItems: result
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            error => {
              this.setState({
                isLoaded: true,
                error
              });
            }
        );


  }

  render() {
    const { error, isLoaded, invoiceItems, transactionsItems } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
          <div>
            <div><h2>Transaction Summary</h2><br />
              {transactionsItems.map(item => (
                  <div class="table" key={item.transactionId}>
                    <div class="tr">
                      <div class="td">Transaction ID : {item.transactionId}</div>
                      <div class="td">Transaction unique No. : {item.transactionUniqueNo}</div>
                      <div class="td">Transaction Date : {item.transactionDate}</div>
                      <div class="td">Description : {item.transactionDesc}</div>
                      <div class="td">Amount : {item.transactionAmount}</div>
                    </div>
                  </div>
              ))}
            </div>

            <br /><br /><br />

            <div><h2>Invoice Summary</h2><br />
              {invoiceItems.map(item => (
                <div class="rTable" key={item.invoiceId}>
                  <div class="rTableRow">
                    <div class="rTableHead">Invoice ID : {item.invoiceId}</div>
                    <div class="rTableHead">Client name : {item.invoiceClientName}</div>
                    <div class="rTableHead">Creation Date : {item.invoiceCreationDate}</div>
                    <div class="rTableHead">Invoice Unique ID : {item.invoiceUniqueNo}</div>
                    <div class="rTableHead">Invoice Creation Date : {item.invoiceCreationDate}</div>
                    <div class="rTableHead">Amount : {item.invoiceAmount}</div>
                    <div class="rTableHead">Status : {item.invoiceStatus}</div>
                  </div>
                </div>
            ))}
            </div>
          </div>

      );
    }
  }
}

export default Summary;
