import React, { Component } from "react";

class Summary extends Component {

  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      invoiceItems: [],
      transactionsItems: []
    };
  }

  componentDidMount() {
    // Fetch transactionSummary
    fetch('http://localhost:8080/transactionsSummary/1000')
        .then(res => res.json())
        .then(
            result => {
              this.setState({
                isLoaded: true,
                transactionsItems: result
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            error => {
              this.setState({
                isLoaded: true,
                error
              });
            }
        );

    // Get Invoice Summary
    fetch('http://localhost:8080/invoiceSummary/1000')
        .then(res => res.json())
        .then(
            result => {
              this.setState({
                isLoaded: true,
                invoiceItems: result
              });
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            error => {
              this.setState({
                isLoaded: true,
                error
              });
            }
        );


  }

  render() {
    const { error, isLoaded, invoiceItems, transactionsItems } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
          <div>
            <div>
              {transactionsItems.map(item => (
                  <li key={item.transactionId}>
                    <ul>Transaction ID : {item.transactionId}</ul>
                    <ul>Transaction unique No. : {item.transactionUniqueNo}</ul>
                    <ul>Transaction Date : {item.transactionDate}</ul>
                    <ul>Description : {item.transactionDesc}</ul>
                    <ul>Amount : {item.transactionAmount}</ul>
                  </li>
              ))}
            </div>

            <ul>{invoiceItems.map(item => (
                <li key={item.invoiceId}>
                  <ul>Invoice ID : {item.invoiceId}</ul>
                  <ul>Client name : {item.invoiceClientName}</ul>
                  <ul>Creation Date : {item.invoiceCreationDate}</ul>
                  <ul>Invoice Unique ID : {item.invoiceUniqueNo}</ul>
                  <ul>Invoice Creation Date : {item.invoiceCreationDate}</ul>
                  <ul>Amount : {item.invoiceAmount}</ul>
                  <ul>Status : {item.invoiceStatus}</ul><br/>
                </li>
            ))}
            </ul>
          </div>

      );
    }
  }
}

export default Summary;
