import React, { Component } from "react";

class Transactions extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  fetchUsers() {
  // Where we're fetching data from
  fetch(`https://jsonplaceholder.typicode.com/users`)
    // We get the API response and receive data in JSON format...
    .then(response => response.json())
    // ...then we update the users state
    .then(data =>
      this.setState({
        users: data,
        isLoading: false,
      })
    )
    // Catch any errors we hit and update the app
    .catch(error => this.setState({ error, isLoading: false }));
  }

  componentDidMount() {
    this.fetchUsers();
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <ul>
          {items.map(item => (
            <li key={item.transactionId}>
              <ul>Transaction ID : {item.transactionId}</ul>
              <ul>Transaction unique No. : {item.transactionUniqueNo}</ul>
              <ul>Transaction Date : {item.transactionDate}</ul>
              <ul>Description : {item.transactionDesc}</ul>
              <ul>Amount : {item.transactionAmount}</ul>
            </li>
          ))};
        </ul>
      );
    }
  }
}

export default Transactions;
