import React, { Component } from "react";

class Invoice extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showPopup: false,
      error: null,
      isLoaded: false,
      items: []
    };
  }

  togglePopup() {
      this.setState({
      showPopup: !this.state.showPopup
      });
    }

  handleClick() {
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
  }

  componentDidMount() {
    fetch("http://localhost:8080/invoices/1000", {mode:'no-cors'})
      .then((res) => res.text())
      .then((text) => text.length ? JSON.parse(text) : {})
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
        })
      .catch((error) => {
        throw error;
      });
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div>

          <ul>{items.map(item => (
              <li key={item.invoiceId}>
                <ul>Invoice ID : {item.invoiceId}</ul>
                <ul>Client name : {item.invoiceClientName}</ul>
                <ul>Creation Date : {item.invoiceCreationDate}</ul>
                <ul>Invoice Unique ID : {item.invoiceUniqueNo}</ul>
                <ul>Invoice Creation Date : {item.invoiceCreationDate}</ul>
                <ul>Amount : {item.invoiceAmount}</ul>
                <ul>Status : {item.invoiceStatus}</ul><br/>
            </li>
          ))}
          </ul>
        </div> 
      );
    }
  }
}


export default Invoice;
